import React, { Fragment, useState } from "react";
import { useDispatch } from "react-redux";
import Button from "../UI/Button";
import Card from "../UI/Card";
import ErrorModal from "../UI/ErrorModal";
import classes from "./AddUsers.module.css";
import { addPerson } from "../../store";

const AddUsers = () => {
  const dispatch = useDispatch();
  const [enteredUserName, setEnteredUserName] = useState("");
  const [enteredAge, setEnteredAge] = useState("");
  const [error, setError] = useState();

  // const enteredUsersName = useSelector((state) => state);
  // const enteredUsersAge = useSelector((state) => state.users.person.age);

  const addUserHandler = (event) => {
    event.preventDefault();

    // const newUser = {
    //   name: enteredUsersName,
    //   age: enteredUsersAge,
    //   id: Math.random().toString(),
    // };

    // if (
    //   enteredUsersName.trim().length === 0 ||
    //   enteredUsersAge.trim().length === 0
    // ) {
    //   setError({
    //     title: "Invalid Input",
    //     message: "Please enter a valid name and age(non-empty values).",
    //   });
    //   return;
    // }
    // if (+enteredUsersAge < 1) {
    //   setError({
    //     title: "Invalid age",
    //     message: "Please enter a valid age(>0).",
    //   });
    //   return;
    // }
    dispatch(addPerson({ enteredUserName, enteredAge }));

    // props.onUsersList(newUser);
    // console.log(enteredUsersName);

    setEnteredUserName("");
    setEnteredAge("");
  };

  const userNameChangeHandler = (event) => {
    setEnteredUserName(event.target.value);
    // dispatch(userActions.setName(event.target.value));
  };

  const ageChangeHandler = (event) => {
    setEnteredAge(event.target.value);
    // dispatch(userActions.setAge(event.target.value));
  };

  const errorHandler = () => {
    setError(null);
  };

  return (
    <Fragment>
      {error && (
        <ErrorModal
          title={error.title}
          message={error.message}
          onConfirm={errorHandler}
        />
      )}
      <Card className={classes.input}>
        <form onSubmit={addUserHandler}>
          <label htmlFor="username">UserName</label>
          <input
            id="username"
            type="text"
            onChange={userNameChangeHandler}
            value={enteredUserName}
          />
          <label htmlFor="age">Age</label>
          <input
            id="age"
            type="number"
            onChange={ageChangeHandler}
            value={enteredAge}
          />
          <Button type="submit">Add User</Button>
        </form>
      </Card>
    </Fragment>
  );
};

export default AddUsers;
